body {
    background: linear-gradient(57deg, #12e6b0, #0d7258, #094456, #062536, #5fbaee);
    background-size: 1000% 1000%;

    -webkit-animation: AnimationName 10s ease infinite;
    -moz-animation: AnimationName 10s ease infinite;
    animation: AnimationName 10s ease infinite;
    text-align: center;
    color: whitesmoke;
}    

@-webkit-keyframes AnimationName {
    0%{background-position:0% 90%}
    50%{background-position:100% 11%}
    100%{background-position:0% 90%}
}
@-moz-keyframes AnimationName {
    0%{background-position:0% 90%}
    50%{background-position:100% 11%}
    100%{background-position:0% 90%}
}
@keyframes AnimationName { 
    0%{background-position:0% 90%}
    50%{background-position:100% 11%}
    100%{background-position:0% 90%}
}

h1 {
    font-size: 70px;
    margin-top: 30px;
    font-family: 'Lobster', cursive;
    color: #e6e5de
      }
#app{
    justify-content: center;
    margin: 0 auto;
}
.increment{
    grid-column-start: 6;
    grid-row-start: 2;
    grid-row-end: 4;
    margin-left: 200px;
    display: inherit;
    margin-top: -60px;
    margin-left: -35px;
    margin-bottom: -60px;
}
.increment-btn{
    margin-bottom: 70px;
}
button{
    align-items: center;
    font-size: 20px;
    width: 70px;
    height: 80px;
    border-radius: 5px;
    color: white;
    background-color: transparent;
    border: 1px solid white;
}
.pomodoro_container{
    display: grid;
    grid-template-columns: 10% 10% 10% 10% 10% 10% 10% 10% 10% 10%;
    grid-template-rows: 25% 25% 25% 25%;
    width: 100%;
    height: auto;
    margin: 0 auto;
}

.timer_container {

    width: 500px;
    height: 500px;
    background-color:rgba(255, 255, 255, 0.068);
    border: 3px solid pink;
    border-radius: 100%;
    grid-column-start: 9;
    grid-column-end: 3;
    grid-row-start: 1;
    grid-row-end: 3;
    margin: 0 auto;
    align-content: center;
    justify-content: center;

}
.clock{
    font-size: 140px;
    margin-top: 160px;
}
.btn_container{
    grid-column-start: 2;
    grid-row-start: 5;
    grid-column-end: 10;
}
.control-btn{
    width: 100px;
    height: 30px;
    margin-right: 10px;
}
#start{
    background-color: #ffffff94;
    color: #0d7258;
    border-width: 0px;
}
